const products = [
  { id: 1, name: "Wireless Headphones", price: 1999, category: "electronics", image: "data:image/jpeg;base64," },
  { id: 2, name: "Smart Watch", price: 2999, category: "electronics", image: "data:image/jpeg;base64," },
  { id: 3, name: "Sneakers", price: 2499, category: "fashion", image: "data:image/jpeg;base64," },
  { id: 4, name: "Backpack", price: 1499, category: "accessories", image: "data:image/jpeg;base64," },
  { id: 5, name: "Sunglasses", price: 999, category: "fashion", image: "data:image/jpeg;base64," }
];

let cart = JSON.parse(localStorage.getItem("cart")) || [];
let filteredProducts = [...products];

const productList = document.getElementById("product-list");
const cartItems = document.getElementById("cart-items");
const totalPrice = document.getElementById("total-price");
const cartCount = document.getElementById("cart-count");
const cartBox = document.getElementById("cart");
const toast = document.getElementById("toast");

/* =========================
   RENDER PRODUCTS
========================= */
function renderProducts() {
  productList.innerHTML = "";
  filteredProducts.forEach(p => {
    productList.innerHTML += `
      <div class="product">
        <img src="${p.image}">
        <h3>${p.name}</h3>
        <p>â‚¹${p.price}</p>
        <button onclick="addToCart(${p.id})">Add to Cart</button>
      </div>
    `;
  });
}

/* =========================
   CART FUNCTIONS
========================= */
function addToCart(id) {
  const existing = cart.find(item => item.id === id);

  if (existing) {
    existing.qty++;
  } else {
    const product = products.find(p => p.id === id);
    cart.push({ ...product, qty: 1 });
  }

  saveCart();
  showToast("Added to cart ðŸ›’");
}

function increaseQty(id) {
  cart.find(item => item.id === id).qty++;
  saveCart();
}

function decreaseQty(id) {
  const item = cart.find(item => item.id === id);
  item.qty--;

  if (item.qty === 0) {
    cart = cart.filter(p => p.id !== id);
  }
  saveCart();
}

function updateCart() {
  cartItems.innerHTML = "";
  let total = 0;
  let count = 0;

  cart.forEach(item => {
    total += item.price * item.qty;
    count += item.qty;

    cartItems.innerHTML += `
      <div class="cart-item">
        <div>
          <strong>${item.name}</strong><br>
          â‚¹${item.price}
        </div>
        <div>
          <button onclick="decreaseQty(${item.id})">âˆ’</button>
          <span>${item.qty}</span>
          <button onclick="increaseQty(${item.id})">+</button>
        </div>
      </div>
    `;
  });

  totalPrice.textContent = total;
  cartCount.textContent = count;
}

function saveCart() {
  localStorage.setItem("cart", JSON.stringify(cart));
  updateCart();
}

/* =========================
   CART TOGGLE
========================= */
function toggleCart() {
  cartBox.classList.toggle("active");
}

/* =========================
   SEARCH & FILTER
========================= */
function searchProducts() {
  const value = searchInput.value.toLowerCase();
  filteredProducts = products.filter(p =>
    p.name.toLowerCase().includes(value)
  );
  renderProducts();
}

function filterCategory(cat) {
  filteredProducts =
    cat === "all"
      ? products
      : products.filter(p => p.category === cat);
  renderProducts();
}

/* =========================
   TOAST
========================= */
function showToast(msg) {
  toast.textContent = msg;
  toast.style.opacity = "1";
  toast.style.transform = "translateY(0)";

  setTimeout(() => {
    toast.style.opacity = "0";
    toast.style.transform = "translateY(10px)";
  }, 2000);
}

/* =========================
   INIT
========================= */
renderProducts();
updateCart();